    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
            // TODO add your handling code here:
            int num = Integer.parseInt(txtNumero.getValue().toString());
            int count = num;
            int fatorial = 1;
            String arm;
            txtResultado.setText("");
            while (num > 0){
               fatorial *=num; 
               arm = txtResultado.getText();
               txtResultado.setText(arm + Integer.toString(num));
               num --;
               if (num > 0){
                   arm = txtResultado.getText();
                   txtResultado.setText(arm + "x");
               } else{
                   arm = txtResultado.getText();
                   txtResultado.setText(arm + "=" + fatorial);
               }              
            }
            
           // txtResultado.setText(Integer.toString(fatorial));
    }//GEN-LAST:event_btnCalcularActionPerformed

